import React, { useState } from 'react';



export default function Live ({liveSong, user, setLive, setSongs}){
    console.log(liveSong)
    const [ scrolling, setScrolling] = useState(false)
    function handleQuit(){
        setLive(false)
        setSongs([])
    }
    return (
        <div className='live'>
           <div className='lines'>
           {user == "admin"? <button className='quitButton' onClick={handleQuit}>quit</button>:null}
            { liveSong.map((line, index)=>(
               <p key={index}>
                { line.map((word, index)=>(
                   <>
                    <span>{word.chords}<sup key={index}>{ word.lyrics }  </sup></span>
                   </>
                ))}
               </p>
            ))}
           </div>
           <button
                style={{
                    position: 'fixed',
                    bottom: '20px',
                    right: '20px',
                    padding: '10px 20px',
                    backgroundColor: scrolling ? 'red' : 'green',
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer',
                }}
                onClick={scrolling ? stopScrolling: startScrolling}
            ></button>
        </div>
    );
};
