import axios from "axios";
import React, { useState } from "react";
import Error from "./Error";
import { io } from 'socket.io-client';

export default function Admin({setSongs}) {
  const socket = io('http://localhost:3001'); 
  const [song, setSong] = useState("");
  const [error, setError] = useState(false);

  function handleSelectSong (song) {
    axios.get(`http://localhost:3001/song/${song}`).then((response)=>{
      setSongs(response.data)
      console.log("sent from admin")
      socket.emit('selectSong', response.data);
  }).catch((error) => {
    if (error.status == 404) {
      setError(true);
    }
  });
  };

  function handleSearch() {
    axios.get(`http://localhost:3001/song/${song}`).then((response)=>{
        setSongs(response.data)
        handleSelectSong(response.data)
    }).catch((error) => {
      if (error.status == 404) {
        setError(true);
      }
    });
  }

  return <div className="admin">
            <h1>Search any song...</h1>
            <div>{error?<Error error={"No results found"}/>:<></>}</div>
            <input className='searchInput' onChange={(e)=>{setSong(e.target.value)}} placeholder='enter song name'></input>
            <br></br>
            <button onClick={handleSelectSong}>Search</button>
         </div>;
}
