import React, { useEffect, useRef, useState } from 'react';
import { io } from 'socket.io-client';


export default function Live({ liveSong, live, user, setLive, setSongs, currUser, setLiveSong }) {
    const lines = useRef(null);
    const [scrolling, setScrolling] = useState(false);
    const [currentSong, setCurrentSong] = useState(null);
    const [socket, setSocket] = useState(null); 

    useEffect(() => {
        const newSocket = io('http://localhost:3001');

        // Listen for connection
        newSocket.on('connect', () => {
            console.log("Socket ID:", newSocket.id); 
        });
        setSocket(newSocket)

        newSocket.on('songSelected',async (song) => {
            console.log("back from socket", song);
            setLiveSong(song);
            setLive(true);
        });

        return () => {
            newSocket.off('songSelected'); 
            newSocket.disconnect(); 
        };
    }, []);

    function handleQuit() {
        setLive(false);
        setSongs([]);
    }

    const startScrolling = () => {
        setScrolling(true);
    };

    const stopScrolling = () => {
        setScrolling(false);
    };

    useEffect(() => {
        let scrollInterval;

        if (scrolling) {
            scrollInterval = setInterval(() => {
                if (lines.current) {
                    lines.current.scrollTop += 1.6; 
                }
            }, 100);
        }

        return () => {
            clearInterval(scrollInterval);
        };
    }, [scrolling]);
    console.log(liveSong)
    return (
      <>
        {sock}
      </>
    );
}
