import React, { useEffect, useRef, useState } from 'react';


export default function Live ({liveSong, user, setLive, setSongs}){
    console.log(liveSong)
    const lines = useRef(null);
    const [ scrolling, setScrolling] = useState(false)
    function handleQuit(){
        setLive(false)
        setSongs([])
    }
    const startScrolling = () => {
        lines.current.scrollBy(1, 1)
        setScrolling(true);
    };

    const stopScrolling = () => {
        setScrolling(false);
    };
    useEffect(() => {
        let scrollInterval;

        if (scrolling) {
            scrollInterval = setInterval(() => {
                if (lines.current) {
                    lines.current.scrollBy(1, 1); // Scroll down by 1 pixel
                }
            }, 100);
        }
        return () => {
            clearInterval(scrollInterval);
        };
    }, [scrolling]);


    return (
        <div className='live' >
           <div className='lines' ref={lines} 
            style={{
                overflowY: 'auto', // Allow vertical scrolling
                maxHeight: '80vh', // Set a max height to make scrolling possible
                border: '1px solid #ccc', // Optional: Add a border for better visibility
                padding: '10px', // Optional: Add padding
            }}
           >
           {user == "admin"? <button className='quitButton' onClick={handleQuit}>quit</button>:null}
           <div>adsda</div>
           </div>
           <button
                style={{
                    position: 'fixed',
                    bottom: '20px',
                    right: '20px',
                    padding: '10px 20px',
                    backgroundColor: scrolling ? 'red' : 'green',
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer',
                }}
                onClick={scrolling ? stopScrolling: startScrolling}
            > {scrolling ? 'Stop Scrolling' : 'Start Scrolling'}</button>
        </div>
    );
};
