import axios from "axios";
import React, { useState } from "react";
import Error from "./Error";
import { ApiUrl } from '../config';

export default function Admin({ setSongs }) {
  const [song, setSong] = useState("");
  const [error, setError] = useState(false);

  // Function to handle song search
  const handleSearch = async () => {
    try {
      const response = await axios.get(`${ApiUrl}/song/${song}`);
      setSongs(response.data);
      setError(false); // reset error state if search is successful
    } catch (error) {
      if (error.response && error.response.status === 404) {
        setError(true);
      } else {
        console.error("An unexpected error occurred:", error);
      }
    }
  };

  return (
    <div className="admin">
      <h1>Search any song...</h1>
      {error && <Error error="No results found" />}
      <input
        className="searchInput"
        onChange={(e) => setSong(e.target.value)}
        placeholder="Enter song name"
      />
      <br />
      <button onClick={handleSearch}>Search</button>
    </div>
  );
}
