import logo from './logo.svg';
import './App.css';
import Signup from './components/Signup';
import Login from './components/Login';
import Admin from './components/Admin';
import Results from './components/Results';
import Player from './components/Player';
import Live from './components/Live';
import AdminSignup from './components/AdminSignup';
import { useState } from 'react';



function App() {
  const [ user, setUser ] = useState("")
  const [ admin, setAdmin ] = useState(false)
  const [ logged, setLogged ] = useState(false)
  const [ status, setStatus ] = useState("login")
  const [ songs, setSongs ] = useState([])
  const [ liveSong, setLiveSong ] = useState([])

  return (
    <div className="App">

     {status == "login"?<Login setUser={setUser} setStatus={setStatus}/>:( admin? <AdminSignup /> :<Signup setStatus={setStatus}/>)}

     {user == "admin"?<Admin />:<></>}
     {user == "player"?<Player />:<></>}
     {songs.length > 0 ? <Results setLiveSong={setLiveSong} songs={songs}/>:<></>}
     <Live liveSong={liveSong}/>
    </div>
  );
}

export default App;
