import React from 'react';



export default function Live ({liveSong, user, setLive, setSongs}){
    console.log(liveSong)
    function handleQuit(){
        setLive(false)
    }
    return (
        <div className='live'>
           <div className='lines'>
           {user == "admin"? <button className='quitButton' onClick={handleQuit}>quit</button>:null}
            { liveSong.map((line, index)=>(
               <p key={index}>
                { line.map((word, index)=>(
                   <>
                    <span>{word.chords}<sup key={index}>{ word.lyrics }  </sup></span>
                   </>
                ))}
               </p>
            ))}
           </div>
        </div>
    );
};
