import React, { useEffect, useRef } from 'react';
import './AutoScroll.css'; // Import your CSS file

const Test = () => {
    const scrollRef = useRef(null);

    // Example content to scroll
    const content = Array.from({ length: 50 }, (_, index) => (
        <p key={index}>
            Line {index + 1}: Lorem ipsum dolor sit amet, consectetur adipiscing elit.
        </p>
    ));

    useEffect(() => {
        const scrollInterval = setInterval(() => {
            if (scrollRef.current) {
                scrollRef.current.scrollTop += 1; // Scroll down by 1 pixel
            }
        }, 100); // Adjust the interval for speed

        return () => {
            clearInterval(scrollInterval); // Cleanup on component unmount
        };
    }, []);

    return (
        <div className='scroll-container'>
            <div className='scroll-content' ref={scrollRef}>
                {content}
            </div>
        </div>
    );
};

export default Test;
