import React, { useEffect, useRef, useState } from 'react';
import { io } from 'socket.io-client';


export default function Live({ liveSong, user, setLive, setSongs, currUser }) {
    const lines = useRef(null);
    const [scrolling, setScrolling] = useState(false);
    const [currentSong, setCurrentSong] = useState(null);
    const [socket, setSocket] = useState(null); // Socket state

    useEffect(() => {
        const newSocket = io('http://localhost:3001'); // Create socket instance

        // Listen for connection
        newSocket.on('connect', () => {
            console.log("Socket ID:", newSocket.id); // Log the socket ID after connection
        });

        setSocket(newSocket); // Set socket to state

        console.log("Socket ID:", newSocket.id); // Check the socket ID

        newSocket.on('songSelected', (song) => {
            console.log("back from socket", song);
            setCurrentSong(song);
        });

        return () => {
            newSocket.off('songSelected'); // Clean up listener
            newSocket.disconnect(); // Disconnect socket on unmount
        };
    }, []);

    function handleQuit() {
        setLive(false);
        setSongs([]);
    }

    const startScrolling = () => {
        setScrolling(true);
    };

    const stopScrolling = () => {
        setScrolling(false);
    };

    useEffect(() => {
        let scrollInterval;

        if (scrolling) {
            scrollInterval = setInterval(() => {
                if (lines.current) {
                    lines.current.scrollTop += 1.6; 
                }
            }, 100);
        }

        return () => {
            clearInterval(scrollInterval);
        };
    }, [scrolling]);

    console.log(currentSong)
    return (
      <>
        <div className='liveSongTitle'><span>{liveSong.name}</span><span>{liveSong.author}</span></div>
        <div className='live'>
             {user === "admin" && (
                    <button className='quitButton' onClick={handleQuit}>quit</button>
                )}
            <div className='lines' ref={lines}>
                {liveSong.song.map((line, index) => (
                   <p className='line' key={index}>
                   {line.map((word, idx) => (
                     <span className='lyrics' key={idx}>
                      {currUser.instrument == "singer"?<></>: <span className='chords'>{word.chords}</span>}
                       <span className='word'>{word.lyrics}</span>
                     </span>
                   ))}
                 </p>
                ))}
            </div>
            <button
            className={`scrollButton ${scrolling ? 'scrolling' : 'not-scrolling'}`}
            onClick={scrolling ? stopScrolling : startScrolling}
            >
                {scrolling ? 'Stop Scrolling' : 'Start Scrolling'}
            </button>
        </div>
      </>
    );
}
