import React, { useEffect, useRef, useState } from 'react';
import { io } from 'socket.io-client';


export default function Live({ liveSong, user, setLive, setSongs, currUser, setLiveSong }) {
    const lines = useRef(null);
    const [scrolling, setScrolling] = useState(false);
    const [currentSong, setCurrentSong] = useState(null);
    const [socket, setSocket] = useState(null); 

    useEffect(() => {
        const newSocket = io('http://localhost:3001'); // Create socket instance

        // Listen for connection
        newSocket.on('connect', () => {
            console.log("Socket ID:", newSocket.id); // Check the socket ID
        });

        newSocket.on('songSelected',async (song) => {
            console.log("back from socket", song);
            setLiveSong(song);
            setLive(true);
        });

        setSocket(newSocket); // Set the socket to state

        return () => {
            newSocket.off('songSelected'); // Clean up listener
            newSocket.disconnect(); // Disconnect socket on unmount
        };
    }, []);

    function handleQuit() {
        setLive(false);
        setSongs([]);
    }

    const startScrolling = () => {
        setScrolling(true);
    };

    const stopScrolling = () => {
        setScrolling(false);
    };

    useEffect(() => {
        let scrollInterval;

        if (scrolling) {
            scrollInterval = setInterval(() => {
                if (lines.current) {
                    lines.current.scrollTop += 1.6; 
                }
            }, 100);
        }

        return () => {
            clearInterval(scrollInterval);
        };
    }, [scrolling]);

    return (
      <>
        <div className='liveSongTitle'><span>{liveSong.name}</span><span>{liveSong.author}</span></div>
        {liveSong}
      </>
    );
}
