import * as React from 'react';
import Box from '@mui/material/Box';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import { FixedSizeList } from 'react-window';
import axios from 'axios';

function handleSelect(selected, setLiveSong){
    axios.get(`http://localhost:3001/song/lyrics/${selected.name}`).then((response)=>{
        console.log(response.data[0].song)
        setLiveSong(response.data[0].song)
    })
}

function renderRow({songs, index, style}) {
const song = songs[index];
  return (
    <ListItem style={style} key={index} component="div" disablePadding>
      <ListItemButton onClick={()=>{handleSelect(song)}}>
        <ListItemText primary={`${index + 1}`} />
        <ListItemText primary={`${song.name}`} value={song.name}/>
        <ListItemText primary={`Author: ${song.author}`} />
      </ListItemButton>
    </ListItem>
  );
}

export default function Results({songs, setLiveSong}) {
  return (
    <Box
      sx={{ width: '100%', height: 400, maxWidth: 400, bgcolor: 'background.paper' }}
    >
      <FixedSizeList
        height={400}
        width={500}
        itemSize={60}
        itemCount={songs.length}
        overscanCount={5}
      >  
        {({ index, style }) => renderRow({ index, style, songs, setLiveSong })}
      </FixedSizeList>
    </Box>
  );
}
