import React, { useEffect, useRef, useState } from 'react';

export default function Live({ liveSong, user, setLive, setSongs }) {
    const lines = useRef(null);
    const [scrolling, setScrolling] = useState(false);

    function handleQuit() {
        setLive(false);
        setSongs([]);
    }

    const startScrolling = () => {
        setScrolling(true);
    };

    const stopScrolling = () => {
        setScrolling(false);
    };

    useEffect(() => {
        let scrollInterval;

        if (scrolling) {
            scrollInterval = setInterval(() => {
                if (lines.current) {
                    lines.current.scrollTop += 1.6; 
                }
            }, 100);
        }

        return () => {
            clearInterval(scrollInterval);
        };
    }, [scrolling]);


    return (
        <div className='live'>
             {user === "admin" && (
                    <button className='quitButton' onClick={handleQuit}>quit</button>
                )}
            <div className='lines' ref={lines}>
                {liveSong.map((line, index) => (
                    <p className='line' key={index}>
                        {line.map((word, idx) => (
                            <span  key={idx}>
                            {word.lyrics}
                        </span>                    
                        ))}
                    </p>
                ))}
            </div>
            <button onClick={scrolling ? stopScrolling : startScrolling} >
                {scrolling ? 'Stop Scrolling' : 'Start Scrolling'}
            </button>
        </div>
    );
}
