import axios from "axios";
import React, { useState } from "react";
import Error from "./Error";
import { ApiUrl } from '../config'

export default function Admin({setSongs}) {
  const [song, setSong] = useState("");
  const [error, setError] = useState(false);

  function handleSearch () {
    axios.post(`${ApiUrl}/song`, {name:"ואיך שלא", song:[[
      {
          "lyrics": "ואיך"
      },
      {
          "lyrics": "שלא",
          "chords": "Em"
      },
      {
          "lyrics": "אפנה"
      },
      {
          "lyrics": "לראות",
          "chords": "Em/D"
      }
  ],
  [
      {
          "lyrics": "תמיד"
      },
      {
          "lyrics": "איתה",
          "chords": "Cmaj7"
      },
      {
          "lyrics": "ארצה"
      },
      {
          "lyrics": "להיות",
          "chords": "G"
      }
  ],
  [
      {
          "lyrics": "שומרת"
      },
      {
          "lyrics": "לי",
          "chords": "Em"
      },
      {
          "lyrics": "היא"
      },
      {
          "lyrics": "אמונים",
          "chords": "Em/D"
      }
  ],
  [
      {
          "lyrics": "לא"
      },
      {
          "lyrics": "מתרוצצת",
          "chords": "Cmaj7"
      },
      {
          "lyrics": "בגנים",
          "chords": "G"
      }
  ],
  [
      {
          "lyrics": "ובלילות",
          "chords": "E"
      },
      {
          "lyrics": "ובלילות",
          "chords": "Em/D"
      }
  ],
  [
      {
          "lyrics": "עולות"
      },
      {
          "lyrics": "עולות",
          "chords": "Cmaj7"
      },
      {
          "lyrics": "בי"
      },
      {
          "lyrics": "מנגינות",
          "chords": "G"
      }
  ],
  [
      {
          "lyrics": "וזרם"
      },
      {
          "lyrics": "דק",
          "chords": "E"
      },
      {
          "lyrics": "קולח",
          "chords": "Em/D"
      }
  ],
  [
      {
          "lyrics": "ותפילותי",
          "chords": "Cmaj7"
      },
      {
          "lyrics": "לרוח"
      },
      {
          "lyrics": "נענות",
          "chords": "G"
      }
  ] ,author:"מוש בן ארי"]}).then((e)=>{console.log(e.status)})

    axios.get(`${ApiUrl}/song/${song}`).then((response)=>{
      setSongs(response.data)
  }).catch((error) => {
    if (error.status == 404) {
      setError(true);
    }
  });
  };

  return <div className="admin">
            <h1>Search any song...</h1>
            <div>{error?<Error error={"No results found"}/>:<></>}</div>
            <input className='searchInput' onChange={(e)=>{setSong(e.target.value)}} placeholder='enter song name'></input>
            <br></br>
            <button onClick={handleSearch}>Search</button>
         </div>;
}
