import React, { useEffect, useRef, useState } from 'react';

export default function Live({ liveSong, user, setLive, setSongs }) {
    const lines = useRef(null);
    const [scrolling, setScrolling] = useState(false);

    function handleQuit() {
        setLive(false);
        setSongs([]);
    }

    const startScrolling = () => {
        setScrolling(true);
    };

    const stopScrolling = () => {
        setScrolling(false);
    };

    useEffect(() => {
        const scrollInterval = setInterval(() => {
            if (lines.current) {
                lines.current.scrollTop += 1; // Scroll down by 1 pixel
            }
        }, 100); // Adjust the interval for speed

        return () => {
            clearInterval(scrollInterval); // Cleanup on component unmount
        };
    }, []);

    const content = Array.from({ length: 50 }, (_, index) => (
        <p key={index}>
            Line {index + 1}: Lorem ipsum dolor sit amet, consectetur adipiscing elit.
        </p>
    ));

    return (
        <div className='live'>
            <div className='lines' ref={lines}>
                {user === "admin" && (
                    <button className='quitButton' onClick={handleQuit}>quit</button>
                )}
                {liveSong.map((line, index) => (
                    <p key={index}>
                        {line.map((word, idx) => (
                           <>
                            <span key={idx}>
                            {word.chords}<sup>{word.lyrics}</sup>
                        </span>

                           </>

                        ))}
                    </p>
                ))}
            </div>
            <button
                style={{
                    position: 'fixed',
                    bottom: '20px',
                    right: '20px',
                    padding: '10px 20px',
                    backgroundColor: scrolling ? 'red' : 'green',
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer',
                }}
                onClick={scrolling ? stopScrolling : startScrolling}
            >
                {scrolling ? 'Stop Scrolling' : 'Start Scrolling'}
            </button>
        </div>
    );
}
