import React, { useEffect, useRef, useState } from 'react';

export default function Live({ liveSong, user, setLive, setSongs }) {
    const lines = useRef(null);
    const [scrolling, setScrolling] = useState(false);

    function handleQuit() {
        setLive(false);
        setSongs([]);
    }

    const startScrolling = () => {
        setScrolling(true);
    };

    const stopScrolling = () => {
        setScrolling(false);
    };

    useEffect(() => {
        let scrollInterval;

        if (scrolling) {
            scrollInterval = setInterval(() => {
                if (lines.current) {
                    console.log("im here")
                    lines.current.scrollIntoView ({  behavior: 'smooth'})
                }
            }, 400);
        }

        return () => {
            clearInterval(scrollInterval);
        };
    }, [scrolling]);

    return (
        <div className='live'>
            <div className='lines' ref={lines}>
                {user === "admin" && (
                    <button className='quitButton' onClick={handleQuit}>quit</button>
                )}
              <div>ad</div>
            </div>
            <button
                style={{
                    position: 'fixed',
                    bottom: '20px',
                    right: '20px',
                    padding: '10px 20px',
                    backgroundColor: scrolling ? 'red' : 'green',
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer',
                }}
                onClick={scrolling ? stopScrolling : startScrolling}
            >
                {scrolling ? 'Stop Scrolling' : 'Start Scrolling'}
            </button>
        </div>
    );
}
